var isValid = false;

var response = op.makeRequest(requestPath);
if (response instanceof Array) {
	for (var i = 0; i < response.length && !isValid; i++) {
		var item = response[i];

		// Get the Type ID and convert to text version
		objTypeId = item.typeDefinitionId;
		objType = op.getTypeById(objTypeId).name;	
		path = item.path;
		parts = system.splitString(path, "/");
		parentId = item.id;
		name = parts[parts.length-1];	

		if (objType == "SOXAccount") {
			// Get the name from the path
			log("==== Checking against: " + path);
			log("====             name: " + name)

			for (var i = 0; i < parts.length && !isValid; i++) {
				var part = parts[i];
				for (var j in validGeos) {
					var geo = validGeos[j];
					if (part.equalsIgnoreCase(geo)) {
						isValid = true;
						break;
					}
				}
			}
		}
	}
}

if (!isValid) {
	log("WARN", "Risk "
		+ work["Risk ID"]
		+ " (" + work["Resource ID"] 
		+ ") under " 
		+ path
		+ " - skipping...");
	system.skipEntry();
}

