// Remove all the attribute which starts with '$', as well as turning LF in
// attribute values to pipe (|)
for(var att in work)
{
	// Remove special attributes (names starting with $)
	if(att.getName().startsWith("$"))
	{
		work.removeAttribute(att);
	}

	// Now look for LF in values, and if found, replace with pipe and update attribute
	var values = [];
	var foundOne = false;
	for (var val in att) {
		if (val.contains("\n")) {
			val = val.replaceAll("\n", "|");
			foundOne = true;
		}
		values.push(val);
	}

	// If LF found then replace modified value
	if (foundOne) {
		log("Replacing LF with | for value(s) of " + att.getName());
		log("Before:\n" + att);
		att.clear();
		for (var i = 0; i < values.length; i++) {
			att.addValue(values[i]);
		}
		log("After:\n" + att);
	}
}

