			"LARGE_STRING_TYPE" : "Large size String (limited to configured maximum)",
			"ENUM_TYPE" : "String",
			"MULTI_VALUE_ENUM" : "String (multi-valued)",
			"CURRENCY_TYPE" : "Number (value in default currency)",
