	timing = timings[i];
	seconds = Math.round(timing/1000);
	minutes = Math.floor(seconds / 60);
	secondsAfter = seconds % 60;
	log("    " + LJ(timingMap.get(timing), 65, i) 
				+ RJ(minutes, 11, i) + " min"
				+ RJ(secondsAfter, 3) + " sec");
}

