displayOrder["glossary"] = 24;

orderIndex = 100;
function getDisplayOrder(description) {
	for (var desc in displayOrder) {
		if (desc.toLowerCase().startsWith(description.toLowerCase())) {
			return displayOrder[desc];
		}
	}
	return orderIndex++;
}

